mapper.select:
SELECT 

FROM qx_channels a

WHERE a.cid = #{cid}

SELECT 

FROM qx_channels a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.cid = #{cid}




		ORDER BY ${page.orderBy}

SELECT 

FROM qx_channels a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE qx_channels SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE cid = #{cid}
