mapper.select:
SELECT m.mid, m.uid, m.user_name, m.headimgurl, m.msg_type, m.anonymity, m.title, m.description, m.business_address, m.business_name, m.price, m.reward, m.requirements, m.pics, m.publish_time, m.location, m.coord_type, m.msg_date,m.msg_end_date,m.msg_2level_type,m.msg_single_type,m.price_des,m.msg_state,m.apply_num,m.seat_num,m.start_place,m.aim_place,m.tribe_id, s.prise_num, s.comment_num FROM ( SELECT * FROM dynamic_msg WHERE mid >= ( ( SELECT MAX(mid) FROM dynamic_msg ) - ( SELECT MIN(mid) FROM dynamic_msg ) ) * RAND() + ( SELECT MIN(mid) FROM dynamic_msg ) AND is_virtual = 1 AND msg_type in
#{item}
LIMIT 20 ) AS m, dynamic_msg_statistic AS s WHERE m.mid = s.mid ORDER BY m.mid DESC

SELECT m.mid, m.uid, m.user_name, m.headimgurl, m.msg_type, m.anonymity, m.title, m.description, m.business_address, m.business_name, m.price, m.reward, m.requirements, m.pics, m.publish_time, m.location, m.coord_type, m.msg_date,m.msg_end_date,m.msg_2level_type,m.msg_single_type,m.price_des,m.msg_state,m.apply_num,m.seat_num,m.start_place,m.aim_place,m.tribe_id, s.prise_num, s.comment_num FROM ( SELECT d.mid, d.uid, d.user_name, d.headimgurl, d.msg_type, d.anonymity, d.title, d.description, d.business_address, d.business_name, d.price, d.reward, d.requirements, d.pics, d.publish_time, d.location, d.coord_type FROM dynamic_msg AS d, system_user AS u WHERE d.mid >= ( ( SELECT MAX(mid) FROM dynamic_msg ) - ( SELECT MIN(mid) FROM dynamic_msg ) ) * RAND() + ( SELECT MIN(mid) FROM dynamic_msg ) AND d.is_virtual = 1 AND d.msg_type in
#{item}
AND d.uid=u.uid AND u.sex=#{sex} LIMIT 20 ) AS m, dynamic_msg_statistic AS s WHERE m.mid = s.mid ORDER BY m.mid DESC

SELECT m.mid, m.uid, m.user_name , m.headimgurl, m.msg_type , m.anonymity, m.title, m.description, m.business_address, m.business_name , m.price, m.reward, m.requirements, m.pics, m.publish_time , m.location, m.coord_type , m.msg_date,m.msg_end_date,m.msg_2level_type,m.msg_single_type,m.price_des,m.msg_state,m.apply_num,m.seat_num,m.start_place,m.aim_place,m.tribe_id, s.prise_num , s.comment_num, s.share_num , s.apply_for_num FROM dynamic_msg AS m, dynamic_msg_statistic AS s WHERE m.mid = s.mid and m.mid in
#{item}
ORDER BY m.mid desc

SELECT m.mid, m.uid, m.user_name, m.headimgurl, m.msg_type, m.anonymity, m.title, m.description, m.business_address, m.business_name, m.price, m.reward, m.requirements, m.pics, m.publish_time, m.location, m.coord_type, m.msg_date,m.msg_end_date,m.msg_2level_type,m.msg_single_type,m.price_des,m.msg_state,m.apply_num,m.seat_num,m.start_place,m.aim_place,m.tribe_id, s.prise_num, s.comment_num, s.share_num , s.apply_for_num FROM dynamic_msg AS m, dynamic_msg_statistic AS s, qx_follow AS f WHERE f.uid = #{uid} AND f.del_flag = 0 AND m.uid = f.follow_uid AND m.msg_single_type in
#{item}
AND m.mid = s.mid ORDER BY m.mid DESC LIMIT 20

