mapper.update:
UPDATE msg_apply
SET check_state = #{checkState} 
WHERE
	mid = #{mid} and uid=#{uid}

UPDATE dynamic_msg SET msg_state= #{msgState}
WHERE
mid = #{mid}

UPDATE dynamic_msg
SET headimgurl = #{headimgurl},
 user_name = #{nickname}
WHERE uid=#{uid}
