mapper.select:
SELECT 

FROM qx_questions_option a

WHERE a.qaid = #{qaid}

SELECT 

FROM qx_questions_option a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.qaid = #{qaid}


		AND a.qid = #{qid}


		AND a.seq_id = #{seqId}




		ORDER BY ${page.orderBy}

SELECT 

FROM qx_questions_option a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE qx_questions_option SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE qaid = #{qaid}
