mapper.select:
SELECT 

FROM qx_questions_results a

WHERE a.qrid = #{qrid}

SELECT 

FROM qx_questions_results a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.qrid = #{qrid}


		AND a.gid = #{gid}




		ORDER BY ${page.orderBy}

SELECT 

FROM qx_questions_results a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE qx_questions_results SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE qrid = #{qrid}
