mapper.select:
SELECT 

FROM qx_message a

WHERE a.msid = #{msid}

SELECT 

FROM qx_message a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.msid = #{msid}


		AND a.to_id = #{toId}


		AND a.from_id = #{fromId}




		ORDER BY ${page.orderBy}

SELECT 

FROM qx_message a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE qx_message SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE msid = #{msid}
