mapper.select:
SELECT 

FROM system_user a

WHERE a.uid = #{uid}

SELECT 

FROM system_user a


	a.del_flag = '0'

		AND a.uid = #{uid}


		AND a.unionid = #{unionid}


		AND a.openid = #{openid}




		ORDER BY ${page.orderBy}

SELECT 

FROM system_user a


	a.del_flag = '0' 

		AND a.uid = #{uid} 


		AND a.unionid = #{unionid} 


		AND a.openid = #{openid} 


 ORDER BY create_time LIMIT 1000

SELECT 

FROM system_user a

WHERE a.login_name = #{loginName}
AND a.`password` = #{password}
AND a.del_flag = '0'

SELECT 

FROM system_user a

WHERE a.mobile = #{mobile}
AND a.`password` = #{password}
AND a.del_flag = '0'

mapper.update:
UPDATE system_user SET 	
	token_local = #{tokenLocal},
	os = #{os}
WHERE uid = #{uid}

UPDATE system_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE uid = #{uid}

UPDATE system_user SET 	
	token_local = #{tokenLocal}
WHERE uid = #{uid}
